//Using hooks for updating in User interface which cannot be done just by simply rendering
import React from "react";
import ReactDOM from "react-dom";

var count=0;
function increase(){
   count++;
   ReactDOM.render(
    <div className="container">
      <h1>{count}</h1>
      <button onClick={increase}>+</button>
    </div>,
    document.getElementById("root")
  );


}
ReactDOM.render(
  <div className="container">
